  {
    id: 4,
    content: 'Site Map',
  },
  {
    id: 5,
    content: 'New',
  },
  {
    id: 6,
    content: 'Sale',
  },
];

const FOOTER_CONTENT_INFO = [
  {
    id: 1,
    content: 'Order Tracking',
  },
  {
    id: 2,
    content: 'Return/Exchange',
  },
  {
    id: 3,
    content: 'Shipping Info',
  },
  {
    id: 4,
    content: 'Account Info',
  },
  {
