    content: 'Disney® Visa® Card',
  },
  {
    id: 6,
    content: 'Personalization Shop',
  },
];

const FOOTER_CONTENT_COPYRIGHT = [
  {
    id: 1,
    copyright: '© Kidsney, All Rights Reserved',
  },
  {
    id: 2,
    copyright: 'Terms of Use',
  },
  {
    id: 3,
    copyright: 'Additional Content Information',
  },
  {
    id: 4,
